from samson.prngs.glfsr import GLFSR
from samson.utilities.bytes import Bytes
import unittest

# Test vectors generated manually using code -> https://en.wikipedia.org/wiki/Shrinking_generator
class GFLSRTestCase(unittest.TestCase):
    def _run_test(self, seed, poly, expected_output):
        glfsr = GLFSR(seed, poly)
        self.assertEqual([glfsr.generate() for _ in range(1000)], expected_output)


